import React from "react";
import movies from '../MovieDummy.js';
import styled from '../styles/movie_style.js'

class MyMovie extends React.Component{
    constructor(props){
        super(props);
        this.mylist = movies.results;
        this.API_URL = 'https://image.tmdb.org/t/p/original/';
        this.state = { isToggleOn : true };
		this.handleClick = this.handleClick.bind(this);
    }
    render(){
        return(
            <styled.wrap_div>
            {this.mylist.map((items, idx) => {
                return (
                <styled.Movie_li key={idx}>
                    <styled.Movie_wrap ><styled.post_img src={this.API_URL + items.poster_path}/></styled.Movie_wrap>
                    <styled.Content_wrap>
                        <div>
                        <styled.Title_span>{items.title}</styled.Title_span>
                        </div>
                        <div>
                        <styled.Rating_span>{items.vote_average}</styled.Rating_span>
                        </div>
                    </styled.Content_wrap>
                </styled.Movie_li>);
            })
            }
            </styled.wrap_div>
        );
    }
}
export default { MyMovie };
