import { useEffect, useState } from "react";
import styled from "styled-components";

function LoginPage() {
    const Login_wrapdiv = styled.div`
        width : 500px;
        height : 700px;
        padding : 20px 50px 50px 20px;
    `;
    const Login_message = styled.h1`
        font-size : 20px;
        font-weight : bold;
    `
    const Login_inputType = styled.span`
        font-size : 10px;
    `
    const Login_input = styled.input`
        width : 600px;
        height : 30px;
        padding : 5px 10px 5px 10px;
        border : 1px solid gray;
        border-radius : 5px 5px 5px 5px;
    `
    const Login_submit = styled.input`
        background-color : #032641;
        color : white;
        width : 500px;
        height : 50px;
        border : none;
        border-radius : 30px 30px 30px 30px;
    `
    const Email_wrap = styled.div`
        padding : 10px 0 0 0;
    `
    const Password_wrap = styled.div`
        padding : 10px 0 0 0;
    `

    const [form, setForm] = useState({
        email : '',
        password : ''    
    });
    const [a, ab] = useState("");
    const settingForm = (e) => {
        ab(e.target.values);
        console.log(e.target.values)
    }
    return(
        <Login_wrapdiv>
            <Login_message>
                이메일과 비밀번호를 입력해주세요
            </Login_message>
            <form>
                <Email_wrap>
                    <div><Login_inputType>이메일 주소</Login_inputType></div>
                    <Login_input type="email" value={a} onChange={e => 
                        settingForm(e)
                    }></Login_input>
                    <div><span>Error Handler</span></div>
                </Email_wrap>
                <Password_wrap>
                    <div><Login_inputType>비밀번호</Login_inputType></div>
                    <Login_input type="password" placeholder="영문 숫자 특수문자 포함 8자 이상"></Login_input>
                    <div><span>Error Handler</span></div>
                </Password_wrap>
                <div>
                    <Login_submit type="submit" value={"확인"}/>
                </div>
            </form>
        </Login_wrapdiv>
    );
}

export default {LoginPage};
