import React from "react";
import movies from '../MovieDummy.js';
import styled from '../styles/movie_style.js'

function MyMovie (){

        const mylist = movies.results;
        const API_URL = 'https://image.tmdb.org/t/p/original/';
        const initialArrayList = Array(mylist.length).fill(false);

        const [maplistArray, changedlistarray] = React.useState(initialArrayList);
        const changeFunction = (idx) => {
            console.log(idx);
        }
        return(
            <styled.wrap_div>
            {mylist.map((items, my_idx) => {
                return (
                <styled.Movie_li key={my_idx} onClick={changeFunction(my_idx)}>
                    <styled.Movie_wrap ><styled.post_img src={API_URL + items.poster_path}/></styled.Movie_wrap>
                    <styled.Content_wrap>
                        <div>
                        <styled.Title_span>{items.title}</styled.Title_span>
                        </div>
                        <div>
                        <styled.Rating_span>{items.vote_average}</styled.Rating_span>
                        </div>
                    </styled.Content_wrap>
                    <styled.shadow_div></styled.shadow_div>
                </styled.Movie_li>);
            })
            }
            </styled.wrap_div>
        );
    }
export default { MyMovie };
