import { useEffect, useState } from "react";


export default function Weather() {
    const mykey = "7d30aeead897238c24c8dd4f72337978";
    const [cityname, setCityname] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [users, setUsers] = useState(null);


    //const Weatherapi = `https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={mykey}`;

    const GetLocation = async() => {
        try{
            setError(null);
			setUsers(null);
			// loading 상태를 true로 변경하여, 현재 loading 중임을 표시
			setLoading(true);
            const response = await fetch(
                `http://api.openweathermap.org/geo/1.0/direct?q=${cityname}&appid=${mykey}`
            );
            const citydata = await response.parse();
            return citydata;

        } catch (error) {
            setError(error);
        } finally {
            setLoading(false);
        }
    }
    useEffect(() => {
        GetLocation();
    }, []);

    if (loading) return <div>로딩중..</div>;
    if (error) return <div>에러가 발생했습니다</div>;

    return (
        <div>
            <input type="text" placeholder="도시를 입력하세요" value={cityname} onChange={e => {setCityname(e.value)
            }}></input>
        </div>
    );
}
